mapper.update:
update course c
set time = (select sum(time) from section where course_id = #{courseId})
where c.id = #{courseId};


update course
set sort = sort + 1
where sort >= #{sortDto.newSort}
  and sort <= #{sortDto.oldSort}
  and id != #{sortDto.id};


update course
set sort = sort - 1
where sort >= #{sortDto.oldSort}
  and sort <= #{sortDto.newSort}
  and id != #{sortDto.id};

update course
set sort = #{sortDto.newSort}
where id = #{sortDto.id};
